    cancelQwenAuth,
    authStatus,
    authMessage,
  } = useQwenAuth(settings, isAuthenticating);

  useEffect(() => {
    if (
      settings.merged.security?.auth?.selectedType &&
      !settings.merged.security?.auth?.useExternal
    ) {
      const error = validateAuthMethod(
